mapper.select:
SELECT content_no, content_id,  content_name, content_post, recom, passwd, content_word, content_crtime, content_mdtime, file1, file1saved, thumb1, size1
FROM brcontent
WHERE content_no = #{content_no}

SELECT content_no, content_id, content_name, content_post, recom, content_view, passwd, content_word, content_crtime, content_mdtime, file1, file1saved, thumb1, size1, r FROM ( SELECT content_no, content_id, content_name, content_post, recom, content_view, passwd, content_word, content_crtime, content_mdtime, file1, file1saved, thumb1, size1, rownum as r FROM ( SELECT content_no, content_id, content_name, content_post, recom, content_view, passwd, content_word, content_crtime, content_mdtime, file1, file1saved, thumb1, size1 FROM brcontent
WHERE (content_name LIKE '%' || #{content_word} || '%' OR content_post LIKE '%' || #{content_word} || '%' OR content_word LIKE '%' || #{content_word} || '%')
ORDER BY content_no DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM brcontent
WHERE content_no=#{content_no} AND passwd=#{passwd}

mapper.update:
UPDATE brcontent
SET content_name=#{content_name}, content_post=#{content_post},  content_word=#{content_word}, content_mdtime=sysdate
WHERE content_no = #{content_no}

UPDATE brcontent
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}, content_mdtime=sysdate
WHERE content_no = #{content_no}

UPDATE brcontent
SET recom = recom + 1
WHERE content_no = #{content_no}

UPDATE brcontent
SET content_view = content_view + 1
WHERE content_no = #{content_no}
